import styled from "styled-components"

const StyledButton = styled.button`
    border: 1px solid ${props => props.theme.color};
    border-radius: 0.5rem;
    padding: 0.5rem;
    width: 100%;
    margin:1rem;
    height: 2rem;
    font-size: 1.5rem;
`

export default StyledButton
