import styled from "styled-components";

const Input = styled.input<{myplaceholder?:string}>`
    border: 1px solid ${props => props.theme.color};
    border-radius: 0.5rem;
    padding: 0.5rem;
    width: 100%;
    height: 2rem;
    font-size: 1.5rem;
    padding:1rem;
    margin:1rem 0;
    position:relative;
    &:focus {
        outline: none;

    }

    &::after{
        content: ${props => props.myplaceholder||' '};
        display:inline-block;
        color:red;
        font-size:inherit;
    }
`;



const StyledInput = <>
    <Input></Input>

</>

export default StyledInput
