import React from 'react'
import { FaBold,FaItalic,FaHeading } from 'react-icons/fa';
import styled from 'styled-components';
import FlexStyled from '../styled/Flex';
import { motion } from 'framer-motion';
const ToolbarWrapper = styled(FlexStyled)`
    background-color: ${props => props.theme.bg};
    border-radius: 0.5rem;

    &>*{
        border:1px solid ${props => props.theme.color};
        width:2rem;
        height:2rem;
        padding: 0.5rem;
        border-radius:0.5rem;
        margin:0 0.3rem;

    }

`   


function Toolbar() {
  return (
      <ToolbarWrapper as={motion.div} direction="row" justify="center" align="center" width="20%" transition={{
          staggerChildren: 0.5
      }}>
          <FaBold /><FaItalic /> <FaHeading />
    </ToolbarWrapper>
  )
}

export default Toolbar
