import FlexStyled from "./styled/Flex";
import Toggle from "./styled/Toggle";


function App() {
  const [t, setTheme] = React.useState<"light"|"dark">('light');

  // The function that toggles between themes
  const toggleTheme = () => {
    // if the theme is not light, then set it to dark
    if (t === 'light') {
      setTheme('dark');
