import { motion } from "framer-motion";
import { useState } from "react";
import styled from "styled-components";

const Input = styled.input<{myplaceholder?:string}>`
    border: 1px solid ${props => props.theme.color};
    border-radius: 0.5rem;
    padding: 0.5rem;
    width: 100%;
    height: 2rem;
    font-size: 1.5rem;
    padding:1rem;
    margin:1rem 0;
    position:relative;
    &:blank{
        outline: none;
        &+label{
            transform:none;
            top:0;
            transition:all 0.5s ease-in;
            left:0;
        }
    }



`;
const StyledLabel = styled(motion.label)`
    position:absolute;
    z-index:10;
    width:100%;
    top:50%;
    left:1rem;
    transform:translateY(-50%);
    transition:0.5s ease-in


`


function StyledInput(props: { myplaceholder: string, id: string, type: string }) {
    const variants = {
        initial: {},
        focused: {
            top: "-1rem",
            left: "-1rem",
            fontSize: "1.5rem",
            transition: {
                duration: 0.3,
                ease
            }

        }
    }
    const [isFocued, setIsFocused] = useState<boolean>(false);
    return (
        <>
            <div style={{position:'relative'}}>
            <Input myplaceholder={props.myplaceholder} id={props.id} type={props.type}/>
            <StyledLabel htmlFor={props.id}>{ props.myplaceholder} </StyledLabel>
            </div>


        </>
  )
}



export default StyledInput
