import { ChangeEvent, ReactEventHandler, SyntheticEvent, useState } from "react";
import Grid from "../styled/Grid";
import GridChild from "../styled/GridChild";
import styled from "styled-components";
import { motion } from "framer-motion";
import ReactMarkdown from "react-markdown";

const WriteArea = styled(GridChild)`
  grid-column-start: 2;
  border: 1px solid ${(props) => props.theme.color || "transparent"};
  width: 100%;
  height: 100%;
  resize: none;
  background-color: ${(props) => props.theme.elementColor || "transparent"};
  color: ${(props) => props.theme.color || "transparent"};
  border-radius: 1rem;
  padding: 1rem;
  font-size: 1rem;
  font-family: inherit;

  letter-spacing: 0.1rem;
  transition: background-color .25s ease-in;


  padding: 1rem;
  &:focus {
    outline: none;
  }

  &::-webkit-scrollbar {
    display: none;
  }
`;

function WorkArea(selectionSetter: (state:{ start: number; end:number})=>never ) {
  const [plainText, setPlainText] = useState<string>("Write something here");
  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setPlainText(e.target.value);
  };
  return (
    <Grid
      templatecolumns="1fr 1fr"
      templaterows="1fr"
      width="100vw"
      height="90%"
      columngap="3rem"
      rowgap="1rem"
      padding="1rem"
    >
      <WriteArea
              as={motion.textarea}
              initial={{ x: "-100%" }}
              animate={{ x: 0 }}
              exit={{ x: "-100%" }}
              transition={{ duration: 1, ease: "easeOut" }}
              placeholder={"Write something here"}
              onChange={handleChange}
              rowstart={1}
              rowend={2}
              columnend={1}
              columnstart={1}
              onSelect={(e:ChangeEvent<HTMLTextAreaElement>) => { console.log(e.target.selectionStart,e.target.selectionEnd) }}
      ></WriteArea>
      <WriteArea
        as={motion.div}
        initial={{ x: "100%" }}
        animate={{ x: 0 }}
        exit={{ x: "100%" }}
        transition={{ duration: 1, ease: "easeOut" }}
        rowstart={1}
        rowend={2}
        columnend={2}
        columnstart={2}
      >
        <ReactMarkdown children={plainText} />
      </WriteArea>
    </Grid>
  );
}

export default WorkArea;
