import styled from "styled-components";

const StyledInput = styled.input<{placeholder:string}>`
    border: 1px solid ${props => props.theme.color};
    border-radius: 0.5rem;
    padding: 0.5rem;
    width: 100%;
    height: 2rem;
    font-size: 1.5rem;
    padding:1rem;
    margin:1rem 0;
    &:focus {
        outline: none;
    }
    &::after{
        content: "";
    }
`;

export default StyledInput
