import { createContext } from "react";

const ThemeContext = createContext<{ theme: "light" | "dark", toggleTheme: () => void }>({
    theme: 'light',
    toggleTheme: function () {
        this.setState({ theme: this.theme === 'light' ? 'dark' : 'light' })
    }
});

export default ThemeContext;
